mapper.select:
SELECT id, pwd, profile, email, regdate, userName, birth, gender
FROM users
WHERE id = #{id}

mapper.update:
UPDATE users
SET pwd = #{newPwd}
WHERE id = #{id}

UPDATE users
SET email = #{email}, profile= #{profile}
WHERE id = #{id}

mapper.select:
SELECT id, email
FROM users
WHERE userName=#{userName}
